"use strict";
Component({
    properties: {
        value: {
            type: String,
            value: ''
        }
    },
    methods: {
        choose: function () {
            var _this = this;
            wx.chooseImage({
                count: 1,
                success: function (res) {
                    var file = res.tempFiles[0];
                    if (file.size > 1024000) {
                        return wx.showToast({
                            title: '文件大小不能超过1M',
                            icon: 'none'
                        });
                    }
                    _this.triggerEvent('input', { value: file.path }, {});
                },
                fail: function (res) {
                    wx.showToast({
                        title: res.errMsg,
                        icon: 'none'
                    });
                }
            });
        }
    }
});
